$(function(){var page_height,position,offset,page;$(window).scroll(function(){page_height=$('.page').eq(0).height();position=$(window).scrollTop();offset=position%page_height;page=Math.floor(position/page_height);if(offset<page_height/2){$('.page').eq(page).intoViewport();}else{$('.page').eq(page+1).intoViewport();}});});